    const json = await //awaitsms async 내부에 있어야 사용 가능

    (
      await fetch(`https://yts.mx/api/v2/movie_details.json?movie_id=${id}`)
    ).json();
    setMovies(json.data.movies);
    // setLoading(false);
  };
  useEffect(() => {
    getMovies();
  }, []);
