import { useState } from "react";
import "./App.css";

function App() {
  const [todo, setTodo] = useState("");
  const [todos, setTodos] = useState([]);

  const onChange = (todo) => {
    setTodo(todo.target.value);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    setTodos([1, todos]);
    console.log(todos);
  };

  return (
    <div>
      <h1>to do list</h1>
      <form onSubmit={onSubmit}>
        <input
          type="text"
          placeholder="write your todo..."
          value={todo}
          onChange={onChange}
        />
        <button>add list</button>
      </form>
      <hr />
      <ul>
        <li></li>
      </ul>
    </div>
  );
}

export default App;
