import { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [coins, setCoins] = useState([]);

  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers").then((response) =>
      response.json().then((json) => {
        // console.log(json);
        setCoins(json);
        setIsLoading(false);
      })
    );
  }, []);

  return (
    <div>
      <h1>The Coins!</h1>
      {isLoading ? (
        <div>Loading...</div>
      ) : (
        <ul>
          {coins.map((coin) => (
            <li key={"ㄴ"}>{coin}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
